USE oa_20160901;CREATE TABLE `user` (  `u_id`          BIGINT       NOT NULL AUTO_INCREMENT,  `u_name`        VARCHAR(255) NOT NULL,  `u_pwd`         VARCHAR(255) NOT NULL,  `u_phoneNum`    VARCHAR(255) NULL,  `u_email`       VARCHAR(255) NULL,  `u_description` VARCHAR(255) NULL,  `regist_date`   DATE         NOT NULL,  `u_pic`         VARCHAR(255) NULL,  `gender`        SMALLINT     NOT NULL,  `nickername`    VARCHAR(255) NULL,  `d_id`          BIGINT       NOT NULL,  `birthday`      DATETIME     NOT NULL,  `work_age`      INT          NULL,  `parent_id`     BIGINT       NOT NULL,  PRIMARY KEY (`u_id`))  ENGINE = InnoDB  DEFAULT CHARSET = utf8;CREATE TABLE `role` (  `r_id`          BIGINT                  NOT NULL  AUTO_INCREMENT,  `r_name`        VARCHAR(255)                  CHARACTER SET utf8                  COLLATE utf8_general_ci NOT NULL,  `r_description` VARCHAR(255)                  CHARACTER SET utf8                  COLLATE utf8_general_ci NULL,  PRIMARY KEY (`r_id`))  ENGINE = InnoDB  DEFAULT CHARSET = utf8;CREATE TABLE `department` (  `d_id`          BIGINT       NOT NULL  AUTO_INCREMENT,  `d_name`        VARCHAR(255) NOT NULL,  `p_id`          BIGINT       NOT NULL,  `d_description` VARCHAR(255) NULL,  PRIMARY KEY (`d_id`))  ENGINE = InnoDB  DEFAULT CHARSET = utf8;CREATE TABLE `role_user` (  `ru_id` BIGINT NOT NULL  AUTO_INCREMENT,  `r_id`  BIGINT NOT NULL,  `u_id`  BIGINT NOT NULL,  PRIMARY KEY (`ru_id`))  ENGINE = InnoDB  DEFAULT CHARSET = utf8;CREATE TABLE `role_privilege` (  `rp_id` BIGINT NOT NULL  AUTO_INCREMENT,  `r_id`  BIGINT NOT NULL,  `p_id`  BIGINT NOT NULL,  PRIMARY KEY (`rp_id`))  ENGINE = InnoDB  DEFAULT CHARSET = utf8;CREATE TABLE `privilege` (  `p_id` BIGINT NOT NULL  AUTO_INCREMENT,  `parent_id`  BIGINT NOT NULL,  `p_name` VARCHAR(255)  NOT NULL,  `p_description` VARCHAR(255),  `p_url` VARCHAR(255),  `icon` VARCHAR(255),  PRIMARY KEY (`p_id`))  ENGINE = InnoDB  DEFAULT CHARSET = utf8;# ALTER TABLE `role_user`#   ADD CONSTRAINT `F_KEY_R_ID` FOREIGN KEY (`r_id`) REFERENCES `role` (`r_id`)#   ON UPDATE CASCADE;## ALTER TABLE `role_user`#   ADD CONSTRAINT `F_KEY_U_ID` FOREIGN KEY (`u_id`) REFERENCES `user` (`u_id`)#   ON UPDATE CASCADE;## ALTER TABLE `department`#   ADD CONSTRAINT `F_KEY_PARENT_KEY` FOREIGN KEY (`p_id`) REFERENCES `department` (`d_id`)#   ON UPDATE CASCADE;## ALTER TABLE `user`#   ADD CONSTRAINT `F_KEY_DEPARTMENT_KEY` FOREIGN KEY (`d_id`) REFERENCES `department` (`d_id`)#   ON UPDATE CASCADE;## ALTER TABLE `user`#   ADD CONSTRAINT `F_KEY_USER_PARENT_KEY` FOREIGN KEY (`parent_id`) REFERENCES `user` (`u_id`)#   ON UPDATE CASCADE;